(61304, 'frFR', 'Plan : Kit de forgeron kyrian', 'Apportez le Plan du kit de forgeron kyrian à Dactylis.', '<Vous avez découvert les plans d\’un kit qui vous permettra de réparer vous-même une pièce d\’armure lors de vos aventures.\n\nDactylis devrait pouvoir vous aider à le fabriquer.>', '', '', '', '', '', '', 39653),
(61303, 'frFR', 'Pluie d\’étincelles', '', '', '', '', '', '', '', '', 39653),
(61272, 'frFR', 'Plan : Charme de discorde', 'Apportez le Plan du charme de discorde à Dactylis.', '<Vous avez découvert les plans d\’un charme qui semble capable d\’infliger des dégâts aux ennemis de vos âmes liées sur la voie de l\’Ascension.\n\nDactylis devrait pouvoir vous aider à le fabriquer.>', '', '', '', '', '', '', 39653),
(61269, 'frFR', 'Plan : Charme de persévérance', 'Apportez le Plan du charme de persévérance à Dactylis.', '<Vous avez découvert les plans d\’un charme qui encourage vos âmes liées à persévérer sur la voie de l\’Ascension.\n\nDactylis devrait pouvoir vous aider à le fabriquer.>', '', '', '', '', '', '', 39653),
