UPDATE `quest_template_locale` SET `QuestDescription`='<L\’enveloppe contient un message de la part de Valeera Sanguinar.>\n\n$n,\n\nJe reviens à l\’instant de Hurlevent, où je me suis entretenue avec le roi Wrynn.\n\nNous avons beaucoup à nous dire.\n\nRejoignez-moi en toute hâte au Grand Sceau.\n\nAl diel shala,\nValeera', `VerifiedBuild`=39653 WHERE (`ID`=58582 AND `locale`='frFR');
